import React, { useRef, useState } from "react"
import "./style/projects.css"
import "./style/myProjects.css"
import weatherApp from "./media/weather.png"
import diam from "./media/diam.png"
import memorycards from "./media/STEINS.png"
import yordles from "./media/yordlee.png"
import lostArk from "./media/lostArk.jpg"
import inventory from "./media/inventory.png"
import posts from "./media/posts.png"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee, faShoppingCart,faUser } from '@fortawesome/free-solid-svg-icons'
import { faGithub } from '@fortawesome/free-brands-svg-icons'
import { useEffect } from "react"


const iconGithub = <FontAwesomeIcon className="iconBrand githubLogoPrev" icon={faGithub} />

const Projects = () => {
  function createProject(id, name, descr, image, tech) {
    return {  id: id, 
      name: name,
      descr: descr,
      image: image,
      tech: tech,
    }
  }

  const darkLandingPage = createProject(1, "Fylo", "Dark landing page", "", ["REACT", "CSS", "JAVASCRIPT"])
  const ocha = createProject(2, "Ocha", "Fake e-commerce website", "", ["REACT", "SASS", "JAVASCRIPT"])
  const weatherApp = createProject(3, "Weather App", "Weather app", "", ["REACT", "SASS", "JAVASCRIPT"])

  const [myProjects, setMyProjects] = useState([darkLandingPage, ocha, weatherApp])


  function renderProjects() {
    return (myProjects.map(myProject => {
      return (
      <div className="project-card">
        <img src="" alt=""></img>
        <div className="project-card-descr">
          <h3 className="title-myProject">{myProject.name}</h3>
          <p className="descr-myProject">{myProject.descr}</p>
          <div className="tech-div">
          {myProject.tech.map(techno => {
            switch(techno) {
              case "REACT":
              return (
              <div className= "techno" style={{color: "blue"}}>{techno}</div>
              )
              case "SASS":
              return (
                <div className= "techno" style={{color: "pink"}}>{techno}</div>
              )
              case "JAVASCRIPT":
              return (
                  <div className= "techno" style={{color: "blue"}}>{techno}</div>
              )
              case "CSS":
              return (
                    <div className= "techno" style={{color: "blue"}}>{techno}</div>
              )
              default: return <div>{techno}</div>;
            }
          })}
          </div>
        </div>

    </div>)

    }))
  }

    return (<div  className="container-projects" id="Projects">
        <h3 className="projects-title">MY PROJECTS</h3>
        <div className="myProjects">{renderProjects()}</div>
    </div>)
}


export default Projects
